import { useEffect, useState } from "react";
import { View, Text, Keyboard, ScrollView } from "react-native";
import TaskInputField from "./src/components/TaskinputField";
import TaskItem from "./src/components/TaskItem"
import styles from "./src/styles/app"

const App = () => {
    const [tasks, setTasks] = useState([])

    const addTask = task => {
        if (task == null) return
        setTasks([...tasks, task])
        Keyboard.dismiss()
    }

    const deleteTask = deleteIndex => {
        setTasks(tasks.filter((value, index) => index != deleteIndex))
    }

    const getTasks = async() => {
        try{
            const response = await fetch('http://localhost:3000/task')
            const data = response.json()
            let array = []
            data.then(
                (val) => console.log(val)
            )
            console.log(array)
            //setTasks(data.rows)
        } catch(error){
            console.error(error)
            setTasks([])
        } 
    }

    useEffect( () => {
        getTasks()
    }, [])

    return (
        <View style={styles.container}>
            <Text style={styles.title}> Lista de Tarefas</Text>
            <ScrollView style={styles.scrollView}>
                {
                    tasks.map((descricao, id) => {
                        return (
                            <View style={styles.taskContainer} key={id}>
                                <TaskItem index={id} task={descricao}
                                    deleteTask={() => deleteTask(descricao)}
                                />
                            </View>
                        )
                    })
                }
            </ScrollView>
            <TaskInputField addTask={addTask} />
        </View>
    )

}

export default App
